```c++
#include <bits/stdc++.h>
using namespace std;

const int N = 1e6 + 5;
const int M = 1e6 + 5;
const int Q = 1e6 + 5;
int n, m, vis[M], bel[N], a[N], st[N], ed[N], cnt[M], ans[N];
int l, r, cur;

struct Question {
    int l, r;
    int num;
} q[Q];

void init() {
    int len = sqrt(n);
    for (int i = 1; i <= len; ++i) {
        st[i] = n / len * (i - 1) + 1;
        ed[i] = n / len * i;
    }
    ed[len] = n;
    for (int i = 1; i <= len; ++i)
        for (int j = st[i]; j <= ed[i]; ++j)
            bel[j] = i;
}

int read() {
    int x = 0, f = 1;
    char c = getchar();
    while (!isdigit(c)) {
        if (c == '-') f = -1;
        c = getchar();
    }
    while (isdigit(c)) x = x * 10 + c - '0', c = getchar();
    return x * f;
}

bool cmp(Question x, Question y) {
    return bel[x.l] == bel[y.l] ? bel[x.r] < bel[y.r] : bel[x.l] < bel[y.l];
}

void add(int pos) {
    if (cnt[a[pos]] == 0)
        ++cur;
    ++cnt[a[pos]];
}

void del(int pos) {
    --cnt[a[pos]];
    if (cnt[a[pos]] == 0)
        --cur;
}

int main() {
    n = read();
    for (int i = 1; i <= n; ++i)
        a[i] = read();
    init();
    m = read();
    for (int i = 1; i <= m; ++i) {
        q[i].l = read();
        q[i].r = read();
        q[i].num = i;
    }
    sort(q + 1, q + 1 + m, cmp);
    l = 1;
    for (int i = 1; i <= m; ++i) {
        while (l > q[i].l) 
            add(--l);
        while (r < q[i].r)
            add(++r);
        while (l < q[i].l) 
            del(l++);
        while (r > q[i].r)
            del(r--);
        ans[q[i].num] = cur;
    }
    for (int i = 1; i <= m; ++i)
        printf("%d\n", ans[i]);
    return 0;
}
