struct Question {
    int l, r;
    int id;
    int lca;
    int ans;
} q[M];

bool cmp2(Question x, Question y) {
    return bel[x.l] == bel[y.l] ? x.r < y.r : bel[x.l] < bel[y.l];
}

int read() {
    int x = 0, f = 1;
