    return x * f;
}

void add(int x, int y) {
    e[++cnt].nxt = head[x];
    e[cnt].to = y;
    head[x] = cnt;
}

void dfs1(int x, int f) {
    dep[x] = dep[f] + 1;
    fa[x] = f;
    siz[x] = 1;
    int Max = -1;
    id[++tot] = x;
    fir[x] = tot;
    for (int i = head[x]; i ; i = e[i].nxt) {
