        dfs2(to, to);
    }
}

int lca(int x, int y) {
    while (top[x] != top[y]) {
        if (dep[top[x]] < dep[top[y]]) std::swap(x, y);
        x = fa[top[x]];
    }
    if (dep[x] < dep[y]) std::swap(x, y);
    return y;
}

void add(int pos) {
