    for (int i = 1; i <= n; ++i)
        a[i] = std::lower_bound(date + 1, date + 1 + n, a[i]) - date;

    int x, y;
    for (int i = 1; i <= n - 1; ++i) {
        x = read(), y = read();
        add(x, y);
        add(y, x);
    }
    dfs1(1, 0);
    dfs2(1, 1);

    int block = sqrt(n);
    for (int i = 1; i <= n * 2; ++i)
        bel[i] = i / block + 1;

    //int x, y;
    int ancestor;
    for (int i = 1; i <= m; ++i) {
        x = read(), y = read();
        if (fir[x] > fir[y]) std::swap(x, y);
        ancestor = lca(x, y);
        q[i].id = i;
        if (ancestor == x) {
            q[i].l = fir[x]; // 欧拉序中的位置
            q[i].r = fir[y];
        } else {
            q[i].l = las[x];
            q[i].r = fir[y];
            q[i].lca = ancestor;
        }
    }

    std::sort(q + 1, q + 1 + m, cmp2);
    int l = 1, r = 0;
    for (int i = 1; i <= m; ++i) {
        while (l < q[i].l) {
            Add(id[l]);
            l++;
        }
        while (r > q[i].r) {
            Add(id[r]);
            r--;
        }
        while (l > q[i].l) {
            l--;
            Add(id[l]);
        }
        while (r < q[i].r) {
            r++;
            Add(id[r]);
        }
        if (q[i].lca)
            Add(q[i].lca);
        q[i].ans = cur;
        // std::cout << "htih" << std::endl;
        if (q[i].lca)
            Add(q[i].lca);
    }

    for (int i = 1; i <= m; ++i)
        ans[q[i].id] = q[i].ans;
    for (int i = 1; i <= m; ++i)
        printf("%d\n", ans[i]);
    return 0;
}
```

